mapper.insert:
INSERT INTO orders (
    order_no, user_id, address_id, order_time, checkout_time,  pay_status, total_amount, remark, phone, consignee, cancel_reason, cancel_time, create_time, update_time, address
) VALUES (#{orderNo}, #{userId}, #{addressId}, #{orderTime}, #{checkoutTime},  #{payStatus}, #{totalAmount}, #{remark}, #{phone}, #{consignee}, #{cancelReason}, #{cancelTime}, #{createTime}, #{updateTime}, #{address})

mapper.select:
select * from orders


        and user_id = #{userId}


        and pay_status = #{payStatus}



order by order_time desc

select * from orders


        and id = #{id}


        and order_no = #{orderNo}


        and user_id = #{userId}


        and pay_status = #{payStatus}


        and pay_method = #{payMethod}
